package fr.hysekai.tokyo.skin;

import com.mojang.authlib.GameProfile;
import com.mojang.authlib.properties.Property;
import fr.hysekai.tokyo.util.Reflection;
import net.minecraft.server.v1_8_R3.*;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.craftbukkit.v1_8_R3.CraftWorld;
import org.bukkit.craftbukkit.v1_8_R3.entity.CraftPlayer;
import org.bukkit.entity.Player;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.SkullMeta;

import java.lang.reflect.Field;
import java.util.HashMap;
import java.util.Map;
import java.util.UUID;
import java.util.concurrent.ThreadLocalRandom;

public enum Skin {
    KUZEN( // 1
            "",
            ""
    ),
    OWL( // 2
            "",
            ""
    ),
    TOKA( // 3
            "",
            ""
    ),
    NISHIKI( // 4
            "",
            ""
    ),
    KAYA( // 5
            "",
            ""
    ),
    ENJI( // 0
            "",
            ""
    ),
    RENJI( // 6
            "",
            ""
    ),
    KING( // 7
            "",
            ""
    ),
    SASAKI( // 8
            "",
            ""
    ),
    KANEKI( // 9
            "",
            ""
    ),
    COLOMBE(new String[][] { // In order 12, 13, 14

            {"",  ""},
            {"", ""},
            {"", ""}

    }),
    APPRENTICE_COLOMBE( // 15
            "",
            ""
    ),
    INNOCENT(new String[][] { // In order 17, 19, 20, 21, 22, 25

            {"", ""},
            {"", ""},
            {"", ""},
            {"", ""},
            {"", ""},
            {"", ""},
    }),
    SAVAGE_GHOUL(new String[][] { // In order 18, 23, 24 26
            {"", ""},
            {"", ""},
            {"", ""},
            {"", ""}
    }),
    COLOMBE_HEAD(
            "",
            null
    ),
    COFFE_HEAD(
            "",
            null
    );


    private final String[][] textures;

    private static final Map<Skin, GameProfile> gameProfiles = new HashMap<>(Skin.values().length, 1);
    private static final Field profileField = Reflection.accessField("org.bukkit.craftbukkit.v1_8_R3.inventory.CraftMetaSkull", "profile");

    Skin(String texture, String signature) {
        this.textures = new String[][] { {texture, signature} };
    }

    Skin(String[][] textures) {
        this.textures = textures;
    }

    public void apply(Player player) {
        String[] textures = this.randomTexture();
        this.apply(player, textures[0], textures[1]);
    }

    private void apply(Player player, String texture, String signature) {
        EntityPlayer entityplayer = ((CraftPlayer) player).getHandle();
        entityplayer.playerConnection.sendPacket(new PacketPlayOutPlayerInfo(PacketPlayOutPlayerInfo.EnumPlayerInfoAction.REMOVE_PLAYER, entityplayer));
        entityplayer.getProfile().getProperties().clear();
        entityplayer.getProfile().getProperties().put("textures", new Property("textures", texture, signature));
        entityplayer.playerConnection.sendPacket(new PacketPlayOutPlayerInfo(PacketPlayOutPlayerInfo.EnumPlayerInfoAction.ADD_PLAYER, entityplayer));
        WorldServer world = ((CraftWorld) player.getWorld()).getHandle();
        entityplayer.playerConnection.sendPacket(new PacketPlayOutRespawn(world.dimension, world.getDifficulty(), world.G(), entityplayer.playerInteractManager.getGameMode()));
        entityplayer.playerConnection.teleport(new Location(world.getWorld(), entityplayer.locX, entityplayer.locY, entityplayer.locZ, entityplayer.yaw, entityplayer.pitch));
        MinecraftServer.getServer().getPlayerList().updateClient(entityplayer);
        entityplayer.playerConnection.sendPacket(new PacketPlayOutExperience(entityplayer.exp, entityplayer.expTotal, entityplayer.expLevel));
        entityplayer.getDataWatcher().watch(10, (byte) 127); // Apply all parts of skin
    }

    public void broadcastPacketsIncludeSelf(Player player) {
        String[] textures = this.randomTexture();
        String texture = textures[0];
        String signature = textures[1];
        this.apply(player, texture, signature);
        this.broadcastPackets(player, texture, signature);
    }

    public void broadcastPackets(Player player) {
        String[] textures = this.randomTexture();
        this.broadcastPackets(player, textures[0], textures[1]);
    }

    private void broadcastPackets(Player player, String texture, String signature) {
        EntityPlayer entityplayer = ((CraftPlayer) player).getHandle();
        Packet<?> removePlayer = new PacketPlayOutPlayerInfo(PacketPlayOutPlayerInfo.EnumPlayerInfoAction.REMOVE_PLAYER, entityplayer);
        entityplayer.getProfile().getProperties().clear();
        entityplayer.getProfile().getProperties().put("textures", new Property("textures", texture, signature));
        Packet<?> addPlayer = new PacketPlayOutPlayerInfo(PacketPlayOutPlayerInfo.EnumPlayerInfoAction.ADD_PLAYER, entityplayer);
        Packet<?> destroyPlayer = new PacketPlayOutEntityDestroy(entityplayer.getId());
        Packet<?> spawnPlayer = new PacketPlayOutNamedEntitySpawn(entityplayer);
        for (EntityPlayer target : MinecraftServer.getServer().getPlayerList().players) {
            if (target == entityplayer) continue;
            target.playerConnection.sendPacket(removePlayer);
            target.playerConnection.sendPacket(addPlayer);
            target.playerConnection.sendPacket(destroyPlayer);
            target.playerConnection.sendPacket(spawnPlayer);
        }
    }

    public ItemStack toSkullItem() {
        GameProfile profile = gameProfiles.get(this);
        if (profile == null) {
            profile = new GameProfile(UUID.randomUUID(), "");
            profile.getProperties().put("textures", new Property("textures", this.textures[0][0]));

            gameProfiles.put(this, profile);
        }

        ItemStack item = new ItemStack(Material.SKULL_ITEM, 1, (short) 3);
        SkullMeta meta = (SkullMeta) item.getItemMeta();
        Reflection.set(meta, profileField, profile);
        item.setItemMeta(meta);
        return item;
    }

    private String[] randomTexture() {
        int length = this.textures.length;
        return length == 1 ? this.textures[0] : this.textures[ThreadLocalRandom.current().nextInt(length)];
    }
}
